userDefinedLinkHintCss:
div > .vimiumHintMarker {
/* linkhint boxes -webkit-gradient(linear, left top, left bottom, color-stop(0%,#FFF785),
  color-stop(100%,#FFC542))*/
background: #002b36;
border: 1px solid #93a1a1;
}

div > .vimiumHintMarker span {
/* linkhint text */
color: #b58900;
/*font-weight: bold;*/
font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
color: #cb4b16;
}
#vomnibar {
background-color: #073642;
}
#vomnibar .vomnibarSearchArea {
background: #073642;
border: solid 1px #93a1a1;
}
#vomnibar #vomnibarInput {
background-color: #002b36;
border: solid 1px #93a1a1;
color: #93a1a1;
}
#vomnibar ul {
background-color: #002b36;
}
#vomnibar li {
border-bottom: solid 1px #93a1a1;
color: red;
}
#vomnibar li.vomnibarSelected {
background-color: #073642;
}

#vomnibar li .vomnibarSource{
color: #859900;
}
#vomnibar ul li .vomnibarMatch {
color: #cb4b16;
}
#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
color: #cb4b16;
}
#vomnibar li .vomnibarUrl {
color: #268bd2
}
#vomnibar li em, #vomnibar li .vomnibarTitle {
color: #93a1a1;
}
